import React from 'react';
import { Field, reduxForm } from 'redux-form';
import Button from './commons/Button';
import InputField from './commons/InputField';
import { formWarn, formValudation } from '../helpers/formValudation';

const validate = values => {
  const errors = {};
  if (!values.username) {
    errors.username = 'Required';
  } else if (values.username.length > 15) {
    errors.username = 'Must be 15 characters or less';
  }
  if (!values.email) {
    errors.email = 'Required';
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = 'Invalid email address';
  }
  if (!values.age) {
    errors.age = 'Required';
  } else if (isNaN(Number(values.age))) {
    errors.age = 'Must be a number';
  } else if (Number(values.age) < 18) {
    errors.age = 'Sorry, you must be at least 18 years old';
  }
  return errors;
};

function LoginForm(props) {
  const { handleSubmit, pristine, submitting } = props;
  const onHandleSubmit = value => {
    console.log(value);
  };

  return (
    <form onSubmit={handleSubmit(onHandleSubmit)} className='login__form'>
      <Field
        name='username'
        type='text'
        component={InputField}
        label='Username'
        id='username'
      />
      <Field
        name='password'
        type='password'
        component={InputField}
        label='Password'
        id='password'
      />
      <div className='login__form--buttons'>
        <Button name='Login' type='submit' disabled={submitting} />
      </div>
    </form>
  );
}

export default reduxForm({
  form: 'loginForm',
  validate: validate, // <--- validation function given to redux-form
  warn: formWarn // <--- warning function given to redux-form,
})(LoginForm);
