import React, { useState } from 'react';
import SidebarMenu from './SidebarMenu';
import Button from './commons/Button';
import icon from '../assets/images/chevron_left.svg';
import { CSSTransition, transit } from 'react-css-transition';
CSSTransition.childContextTypes = {
  // this can be empty
};
const transitionStyles = {
  defaultStyle: {
    transform: 'translate(0, 0)'
  },
  enterStyle: {
    transition: 'all 0.5s ease',
    marginLeft: '-200px'
  },
  leaveStyle: {
    transform: transit('translate(0, 0)', 500, 'ease-in-out')
  },
  activeStyle: {
    transform: 'translate(15px, 0)',
    marginLeft: '-200px'
  }
};
function Sidebar(props) {
  const [active, setActive] = useState(false);
  const onHandleActive = () => {
    setActive(!active);
    props.onHandleSidebar(false);
  };
  return (
    <CSSTransition {...transitionStyles} active={active}>
      <div className='sidebar'>
        <SidebarMenu />
        <div>
          <Button onClick={onHandleActive} icon={icon} />
        </div>
      </div>
    </CSSTransition>
  );
}

export default Sidebar;
