import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Button from './Button';
import { hideMessages } from '../../actions/messageAction';

function Messages() {
  const messages = useSelector(state => state.messages);
  const dispatch = useDispatch();

  useEffect(() => {
    if (messages.message !== '') {
      setTimeout(() => {
        dispatch(hideMessages());
      }, 2000);
    }
  }, [dispatch, messages.message]);

  if (messages.message !== '') {
    return (
      <div className='messages__container'>
        <div className={`messages ${messages.type ? messages.type : ''}`}>
          <div>
            <h5>
              {messages.type.charAt(0).toLocaleUpperCase() +
                messages.type.slice(1)}
            </h5>
            {messages.message}
          </div>
          <Button icon='mdi-close' onClick={() => dispatch(hideMessages())} />
        </div>
      </div>
    );
  }
  return null;
}

export default Messages;
