import React from 'react';
import { Field, reduxForm } from 'redux-form';
import InputField from './commons/InputField';
import Button from './commons/Button';
import { formValidator, formWarn } from '../helpers/formValudation';
import { Link } from 'react-router-dom';

function LoignForm(props) {
  const { handleSubmit, pristine, submitting } = props;
  const onHandleSubmit = value => {
    // onHandleFormSubmit(value);
  };
  return (
    <div className='authForm'>
      <header className='authForm__header'>
        <h2>Login to your accounts!</h2>
        <p>
          Welcome to pixto, Start your create a your bussiness account to better
          clients
        </p>
      </header>
      <form onSubmit={handleSubmit(onHandleSubmit)}>
        <Field
          name='signphotogrphmobile'
          component={InputField}
          type='number'
          label='Mobile Number'
        />
        <Field
          name='signUpPassword'
          component={InputField}
          type='password'
          label='Password'
        />
        <div className='authForm__forget'>
          <Link to='/forget-password'>Forget Password ?</Link>
        </div>
        <div className='authForm__button'>
          <Button
            name='Login'
            disabled={pristine || submitting}
            type='submit'
          />
        </div>
        <div className='authForm__otp'>
          <Link to='/login-otp'>Login with OTP</Link>
        </div>
      </form>
    </div>
  );
}

export default reduxForm({
  form: 'loignForm',
  validate: formValidator,
  warn: formWarn
})(LoignForm);
