import React, { Fragment, useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';
import { CSSTransition, transit } from 'react-css-transition';

CSSTransition.childContextTypes = {
  // this can be empty
};
const transitionStyles = {
  defaultStyle: {
    transform: 'translate(0, 0)'
  },
  enterStyle: {
    transform: transit('translate(-100px, 0)', 500, 'ease-in-out')
  },
  leaveStyle: {
    transform: transit('translate(0, 0)', 500, 'ease-in-out')
  },
  activeStyle: {
    transform: 'translate(-100px, 0)'
  }
};

const HighOrderLayout = WrappedComponent => {
  const [hideSidebar, setHideSideBar] = useState(true);
  const onHandleSidebar = () => {
    setHideSideBar(false);
  };
  const onHandleHeader = () => {
    setHideSideBar(true);
  };

  useEffect(() => {
    document.body.classList.add('dashboard');
    return () => {
      document.body.classList.remove('dashboard');
    };
  }, []);

  const hocComponent = ({ ...props }) => {
    return (
      <Fragment>
        <Header showMenu={!hideSidebar} onHandleHeader={onHandleHeader} />
        <div className={`highOrderLayout ${hideSidebar ? 'open' : 'hide'}`}>
          <CSSTransition {...transitionStyles} active={hideSidebar}>
            <Sidebar onHandleSidebar={onHandleSidebar} />
          </CSSTransition>
          <div className='wrapper'>
            <WrappedComponent {...props} />
          </div>
        </div>
      </Fragment>
    );
  };

  return hocComponent;
};

export default HighOrderLayout;
