import React from 'react';
import SidebarMenu from './SidebarMenu';
import Button from './commons/Button';
import icon from '../assets/images/chevron_left.svg';
import { CSSTransition, transit } from 'react-css-transition';
const transitionStyles = {
  defaultStyle: {
    transform: 'translate(0, 0)'
  },
  enterStyle: {
    transform: transit('translate(175px, 0)', 500, 'ease-in-out')
  },
  leaveStyle: {
    transform: transit('translate(0, 0)', 500, 'ease-in-out')
  },
  activeStyle: {
    transform: 'translate(175px, 0)'
  }
};
function Sidebar(props) {
  return (
    <CSSTransition {...transitionStyles} active={props.active}>
      <div className='sidebar'>
        <SidebarMenu />
        <div>
          <Button onClick={props.onHandleSidebar} icon={icon} />
        </div>
      </div>
    </CSSTransition>
  );
}

export default Sidebar;
