export const formValidate = values => {
  const errors = {};

  if (!values.eventName) {
    errors.eventName = 'Please enter Event name';
  }
  if (!values.albumName) {
    errors.albumName = 'Please enter Album name';
  }
  if (!values.clientName) {
    errors.clientName = 'Please enter Client name';
  }
  if (!values.clientAdress) {
    errors.clientAdress = 'Please enter Client Address';
  }
  if (!values.clientPincode) {
    errors.clientPincode = 'Please enter Client Pincode';
  }
  if (!values.shareName) {
    errors.shareName = 'Please enter Client name';
  }
  if (!values.shareLink) {
    errors.shareLink = 'Please enter Share name';
  }
  if (!values.shareMessage) {
    errors.shareMessage = 'Message is required';
  }
  if (!values.shareNumber) {
    errors.shareNumber = 'Mobile Number is Required';
  } else if (isNaN(Number(values.shareNumber))) {
    errors.shareNumber = 'Must be a number';
  } else if (values.shareNumber.length !== 10) {
    errors.shareNumber = 'Mobile Number must be 10 Numbers';
  }
  if (!values.sharePrivacyPassword) {
    errors.sharePrivacyPassword = 'Password is required';
  }
  if (!values.shareEmail) {
    errors.shareEmail = 'Please enter Client Email';
  }
  if (!values.clientContact) {
    errors.clientContact = 'Mobile Number is Required';
  } else if (isNaN(Number(values.clientContact))) {
    errors.clientContact = 'Must be a number';
  } else if (values.clientContact.length !== 10) {
    errors.clientContact = 'Mobile Number must be 10 Numbers';
  }
  if (!values.loginphotoemail) {
    errors.loginphotoemail = 'Email is Required';
  } else if (
    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.loginphotoemail)
  ) {
    errors.loginphotoemail = 'Invalid email address';
  }
  if (!values.loginclientotp) {
    errors.loginclientotp = 'OTP is Required';
  } else if (isNaN(Number(values.loginclientotp))) {
    errors.loginclientotp = 'Must be a number';
  } else if (values.loginclientotp.length > 6) {
    errors.loginclientotp = 'OTP must be 6 Numbers';
  }

  if (!values.loginclientmobile) {
    errors.loginclientmobile = 'Mobile Number is Required';
  } else if (isNaN(Number(values.loginclientmobile))) {
    errors.loginclientmobile = 'Must be a number';
  } else if (values.loginclientmobile.length !== 10) {
    errors.loginclientmobile = 'Mobile Number must be 10 Numbers';
  }

  if (!values.singUpName) {
    errors.singUpName = 'Please enter Full name';
  }

  // if (!values.signclientemail) {
  //   errors.signclientemail = 'Email is Required';
  // } else if (
  //   !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.signclientemail)
  // ) {
  //   errors.signclientemail = 'Invalid email address';
  // }

  if (!values.signUpMobile) {
    errors.signUpMobile = 'Mobile Number is Required';
  } else if (isNaN(Number(values.signUpMobile))) {
    errors.signUpMobile = 'Must be a number';
  } else if (values.signUpMobile.length !== 10) {
    errors.signUpMobile = 'Mobile Number must be 10 Numbers';
  }

  if (!values.signphotogrphname) {
    errors.signphotogrphname = 'Please enter name';
  }
  if (!values.signclientStudioName) {
    errors.signclientStudioName = 'Please enter Business name';
  }

  if (!values.signphotogrphmobile) {
    errors.signphotogrphmobile = 'Mobile Number is Required';
  } else if (isNaN(Number(values.signphotogrphmobile))) {
    errors.signphotogrphmobile = 'Must be a number';
  } else if (values.signphotogrphmobile.length !== 10) {
    errors.signphotogrphmobile = 'Mobile Number must be 10 Numbers';
  }

  if (!values.signphotogrphemail) {
    errors.signphotogrphemail = 'Email is Required';
  } else if (
    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.signphotogrphemail)
  ) {
    errors.signphotogrphemail = 'Invalid email address';
  }

  if (!values.signUpPassword) {
    errors.signUpPassword = 'Password is Required';
  } else if (values.signUpPassword.length < 3) {
    errors.signUpPassword = 'Password must be 4 characters';
  } else if (values.signphotogrphpassword !== values.signphotogrphpassword2) {
    errors.signphotogrphpassword2 = 'Please match Password';
  }

  if (!values.signclientcity) {
    errors.signclientcity = 'Please choose current City';
  }

  if (!values.username) {
    errors.username = 'Please choose current City';
  }

  if (!values.password) {
    errors.password = 'Please choose current City';
  }

  return errors;
};

export const formWarn = values => {
  const warnings = {};

  return warnings;
};
