export const formValudation = values => {
  const errors = {};
  if (!values.username) {
    errors.username = 'Required Username';
  }
  if (!values.password) {
    errors.password = 'Required Password';
  }
  return errors;
};

export const formWarn = values => {
  const warnings = {};
  return warnings;
};
