import React, { useState } from 'react';
import SidebarMenu from './SidebarMenu';
import Button from './commons/Button';
import icon from '../assets/images/chevron_left.svg';
import { CSSTransition, transit } from 'react-css-transition';
CSSTransition.childContextTypes = {
  // this can be empty
};
const transitionStyles = {
  defaultStyle: {
    transform: 'translate(0, 0)'
  },
  enterStyle: {
    transform: transit('translate(-200px, 0)', 500, 'ease-in-out')
  },
  leaveStyle: {
    transform: transit('translate(0, 0)', 500, 'ease-in-out')
  },
  activeStyle: {
    transform: 'translate(-200px, 0)'
  }
};
function Sidebar(props) {
  const [active, setActive] = useState(false);
  return (
    <CSSTransition {...transitionStyles} active={active}>
      <div className='sidebar'>
        <SidebarMenu />
        <div>
          <Button onClick={() => setActive(!active)} icon={icon} />
        </div>
      </div>
    </CSSTransition>
  );
}

export default Sidebar;
