import { LOGIN__USER, LOGOUT__USER } from '../constants/actionTypes';
import axios from 'axios';
import { showMessages } from './messageAction';

export const loginAction = value => async dispatch => {
  try {
    const response = await axios.post('http://52.76.240.170/login', value);
    console.log(response);
    if (!response.access_token) {
      dispatch(showMessages('error', response.data));
    } else {
      dispatch({
        type: LOGIN__USER,
        payload: value
      });
      dispatch(showMessages('success', 'Successfuly Login'));
    }
  } catch (error) {
    dispatch(showMessages('error', error.message));
  }
};

export const logoutAction = () => ({
  type: LOGOUT__USER
});
