export const validate = values => {
  const errors = {};
  if (!values.username) {
    errors.username = 'Required Username';
  } else if (values.username.length > 6) {
    errors.username = 'Must be Username 6 characters or less';
  }
  if (!values.password) {
    errors.password = 'Required Password';
  } else if (values.password.length > 6) {
    errors.password = 'Must be Password 6 characters or less';
  }
  return errors;
};

export const warn = values => {
  const warnings = {};

  return warnings;
};
