export const formValidator = values => {
  const errors = {};
  if (!values.userName) {
    errors.userName = 'Required Username';
  }
  if (!values.userPassword) {
    errors.userPassword = 'Required Password';
  }
  return errors;
};

export const formWarn = values => {
  const warnings = {};
  return warnings;
};
