import React, { Fragment, useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';

const HighOrderLayout = WrappedComponent => {
  const [hideSidebar, setHideSideBar] = useState(true);
  const onHandleSidebar = () => {
    setHideSideBar(false);
    // document.querySelector('.sidebar').style.marginLeft = '-200px';
  };
  const onHandleHeader = () => {
    setHideSideBar(true);
    // document.querySelector('.sidebar').style.marginLeft = '0';
  };

  useEffect(() => {
    document.body.classList.add('dashboard');
    if (!hideSidebar) {
      document.querySelector('.sidebar').style.marginLeft = '-200px';
    } else {
      document.querySelector('.sidebar').style.clear = none;
      document.querySelector('.sidebar').style.marginLeft = '0px';
    }
    console.log(hideSidebar);
    return () => {
      document.body.classList.remove('dashboard');
      if (hideSidebar) {
        document.querySelector('.sidebar').style.marginLeft = '0px';
      }
    };
  }, [hideSidebar]);
  const hocComponent = ({ ...props }) => {
    return (
      <Fragment>
        <Header showMenu={!hideSidebar} onHandleHeader={onHandleHeader} />
        <div className={`highOrderLayout ${hideSidebar ? 'open' : 'hide'}`}>
          <Sidebar onHandleSidebar={onHandleSidebar} active={hideSidebar} />
          <div className='wrapper'>
            <WrappedComponent {...props} />
          </div>
        </div>
      </Fragment>
    );
  };

  return hocComponent;
};

export default HighOrderLayout;
