import React from 'react';
import SidebarMenu from './SidebarMenu';
import Button from './commons/Button';
import { Transition } from 'react-transition-group';

class Sidebar extends React.Component {
  render() {
    const duration = 1000;
    const sidebarStyle = {
      transition: `width ${duration}ms`
    };
    const sidebarTransitionStyles = {
      entering: { width: 0 },
      entered: { width: '200px' },
      exiting: { width: '200px' },
      exited: { width: 0 }
    };
    const linkStyle = {
      transition: `opacity ${duration}ms`
    };
    const linkTransitionStyles = {
      entering: { opacity: 0 },
      entered: { opacity: 1 },
      exiting: { opacity: 1 },
      exited: { opacity: 0 }
    };
    return (
      <Transition in={this.props.active} timeout={duration}>
        {state => (
          <div
            className='sidebar'
            style={{
              ...sidebarStyle,
              ...sidebarTransitionStyles[state]
            }}>
            <SidebarMenu />
            <Button onClick={this.props.onHandleSidebar} name='Arrow' />
          </div>
        )}
        ;
      </Transition>
    );
  }
}

export default Sidebar;
