import React, { Fragment, useState } from 'react';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';
import { Transition } from 'react-transition-group';
import { CSSTransitionGroup } from 'react-transition-group';

const HighOrderLayout = WrappedComponent => {
  const [hideSidebar, setHideSideBar] = useState(true);
  const hocComponent = ({ ...props }) => {
    const onHandleSidebar = () => {
      setHideSideBar(false);
    };
    const onHandleHeader = () => {
      setHideSideBar(true);
    };
    return (
      <Fragment>
        <Header showMenu={!hideSidebar} onHandleHeader={onHandleHeader} />
        <div className={`highOrderLayout`}>
          <Transition in={hideSidebar} timeout={300}>
            <Sidebar onHandleSidebar={onHandleSidebar} />
          </Transition>
          <div className='wrapper'>
            <WrappedComponent {...props} />
          </div>
        </div>
      </Fragment>
    );
  };

  return hocComponent;
};

export default HighOrderLayout;
